mapper.update:
update discuss_post
set comment_count=#{commentCount}
where id = #{id}

update discuss_post
set type = #{type}
where id = #{id}

update discuss_post
set status = #{status}
where id = #{id}

update discuss_post
set score = #{score}
where id = #{id}

mapper.select:
select

from discuss_post
where status != 2 #被删除的帖子不可见

    and user_id = #{userId}

order by type desc, create_time desc
limit #{offset}, #{limit}

select

from discuss_post
where status != 2 #拉黑用户不可见

    and user_id = #{userId}

order by type desc, score desc
limit #{offset}, #{limit}
